tool_input.content:
interface Testimonial {
  author: string
  content: string
  rating: 5 | 4
}

interface TestimonialsProps {
  testimonials: Testimonial[]
}

export function Testimonials({ testimonials }: TestimonialsProps) {
  return (
    <section className="mt-16" aria-label="Avis des lecteurs">
      <h2 className="font-display text-center text-3xl font-bold tracking-tight text-zinc-800 dark:text-zinc-100">
        Ce que disent les lecteurs
      </h2>
      <div className="mt-8 grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
        {testimonials.map((testimonial, index) => (
          <div
            key={index}
            className="rounded-2xl border border-zinc-100 bg-white p-6 dark:border-zinc-700/40 dark:bg-zinc-800/50"
          >
            {/* Rating stars */}
            <div className="mb-4 flex gap-1" aria-label={`${testimonial.rating} étoiles sur 5`}>
              {Array.from({ length: testimonial.rating }).map((_, i) => (
                <span key={i} className="text-[#f0bb48]" aria-hidden="true">
                  ⭐
                </span>
              ))}
            </div>

            {/* Content */}
            <blockquote className="text-base italic leading-relaxed text-zinc-600 dark:text-zinc-400">
              &ldquo;{testimonial.content}&rdquo;
            </blockquote>

            {/* Author */}
            <footer className="mt-4 text-sm font-semibold text-zinc-900 dark:text-zinc-100">
              — {testimonial.author}
            </footer>
          </div>
        ))}
      </div>
    </section>
  )
}

